mapper.update:
update user
set username=#{username}, tel=#{tel}, phone=#{phone}, zipcode=#{zipcode}, address=#{address}, email=#{email} 
where id=#{id}

mapper.select:
select id, account, username, password, tel, phone, zipcode, address, email
from user
where id=#{id}

select id, account, username, password, tel, phone, zipcode, address, email
from user
where account=#{account} and password=#{password}
