mapper.update:
update product
set name=#{name}, price=#{price}, menuid=#{menuid}, submenuid=#{submenuid}, 
details=#{details}, factory=#{factory}
where id=#{id}

mapper.select:
select id, name, price, menuid, submenuid, details, factory
from product
where id=#{id}

select id, name, price, menuid, submenuid, details, factory
from product
where menuid=#{menuid}

select id, name, price, menuid, submenuid, details, factory
from product
where submenuid=#{submenuid}
